/**
  * @brief  The application entry point.
  * @retval int
  */
int main(void)
{
  /* USER CODE BEGIN 1 */

  /* USER CODE END 1 */
  

  /* MCU Configuration--------------------------------------------------------*/

  /* Reset of all peripherals, Initializes the Flash interface and the Systick. */
  HAL_Init();

  /* USER CODE BEGIN Init */

  /* USER CODE END Init */

  /* Configure the system clock */
  SystemClock_Config();

  /* USER CODE BEGIN SysInit */

  /* USER CODE END SysInit */

  /* Initialize all configured peripherals */
  MX_GPIO_Init();
  MX_DAC1_Init();
  MX_TIM2_Init();
  MX_USART1_UART_Init();
  /* USER CODE BEGIN 2 */
	
	//RECEIVE FROM UART
	HAL_UART_Receive_IT(&huart1,  dataBuff, 4000);
	

	HAL_DAC_Start(&hdac1, DAC1_CHANNEL_1);
	HAL_DAC_Start(&hdac1, DAC1_CHANNEL_2);
	
	HAL_TIM_Base_Start_IT(&htim2);
	
  /* USER CODE END 2 */
	
 

  /* Infinite loop */
  /* USER CODE BEGIN WHILE */
  while (1)
  {
		
		if(HAL_GPIO_ReadPin(GPIOC, GPIO_PIN_13) ==GPIO_PIN_RESET){
			playFlag = 1;
			
		}
		
		while(playFlag)
		{
			//turn on LED while playing
			HAL_GPIO_WritePin(GPIOB,GPIO_PIN_14,GPIO_PIN_SET);
			if(sampleFlag == 1)
			{
				sampleFlag = 0;
				
				if(i < 4000)
				{
					//range must fit into 12B dac, scale array elements (in matlab?)
					// DAC_OUTx = VREF+ * DOR / 4095
					
					HAL_DAC_SetValue(&hdac1, DAC_CHANNEL_1,DAC_ALIGN_8B_R,audio[i] );
					HAL_DAC_SetValue(&hdac1, DAC_CHANNEL_2,DAC_ALIGN_8B_R,audio[i] );
					
					i++;
				}else
				{
					//turn off LED
					HAL_GPIO_WritePin(GPIOB,GPIO_PIN_14,GPIO_PIN_RESET);
					//Reset counters and flags
					i=0;
					playFlag = 0;
				}
				
				
				
			}
		}
		

    /* USER CODE END WHILE */

    /* USER CODE BEGIN 3 */
  }
  /* USER CODE END 3 */
}

